import React from 'react';
import { connect } from 'react-redux';
import coinsActions from '../../Redux/CoinsList/actions'
import coinsDetailsActions from '../../Redux/CoinDetails/actions'
import {columns} from './utils'
import PropTypes from 'prop-types';
import Link from 'next/link'
import {starFull} from 'react-icons-kit/icomoon/starFull'
import { Icon } from 'react-icons-kit'
import { Tabs, Tab, Spinner} from 'react-bootstrap'
import {addFavoritList} from '../../helpers/utils'
import ListTable from '../../Components/ListTable'

class List extends React.Component {

    static propTypes = {
        list        : PropTypes.array.isRequired,
        UI          : PropTypes.object.isRequired,
        detailsUI   : PropTypes.object.isRequired,
        listReload  : PropTypes.func.isRequired,
        uiRefresh   : PropTypes.func.isRequired,
    }

    static getInitialProps() {}

    componentDidMount(){
        window.addEventListener('scroll', this.listenToScroll);
        const {listReload, uiRefresh} = this.props;
        let favorits = localStorage.getItem('favorits');
        if(favorits) {
            favorits = JSON.parse(favorits);
        } else {
            favorits = []
        }
        uiRefresh({favorits})
        listReload();
    }

    listenToScroll = () => {
        if(window.innerHeight + document.documentElement.scrollTop
            === document.documentElement.offsetHeight) {
                this.loadMoreCoins();
        }
      }

    loadMoreCoins = () => {
        const { listReload, uiRefresh, UI } = this.props;
        const { skip } = UI;
        uiRefresh({
            skip : skip + 1, 
        })
        listReload();
    }
    renderValue = (columnItem, listItem, index) => {
        const {UI, uiRefresh, listReload} = this.props;
        const { favorits,  } = UI;
        let liked = favorits.includes(listItem.id) ? 'active' : 'inactive';
        switch (columnItem.dataIndex) {
            case 'id':
                return index + 1;
            case 'name':
                return <>
                            <div className = {`starList ${liked}`}>
                                <Icon icon={starFull} size={20} onClick={() =>this.addFavoritList(listItem.id)}/>
                            </div>
                            <Link href={`/coins/${listItem.id}`}>
                                <a>
                                    <img src={listItem.icon} className = 'coinIcon'/>
                                    {listItem[columnItem.dataIndex]}
                                </a>
                            </Link>
                        </>
            default : 
            return listItem[columnItem.dataIndex]
        }
    }

    addFavoritList = (id) => {
        const { uiRefresh} = this.props;
        const favorits = addFavoritList(id);
        uiRefresh ({favorits});
    }

    render() {
        const { list, UI, detailsUI } = this.props;
        const { loading, favorits } = UI;
        const favoritsList = list.filter(item => favorits.includes(item.id));
        // console.log(favoritsList);
        return (
            <Tabs defaultActiveKey="Coins" transition={false}>
                <Tab eventKey="Coins" title="Coins">
                    <ListTable 
                        columns={columns}
                        list={list}
                        UI={UI}
                        listReload={listReload}
                        uiRefresh={uiRefresh}
                    />
                    {/* <div className='table'>                
                        {columns.map((columnItem, index) => (
                            <div className='cols' key={`${columnItem.key}_${index}`}>
                                <div className='colTitle'>{columnItem.title}</div>
                                <div className = 'rows'>
                                    {list.map((listItem, index) => <div className='rowItem' key={`${listItem.id}_${index}`}>
                                        {this.renderValue(columnItem, listItem, index)}
                                    </div>)}
                                </div>
                            </div>)
                        )}
                        {loading && <Spinner animation="border" variant="secondary" className='spinner'/>}
                        <button onClick={this.loadMoreCoins} className='showMore'>Show More</button>
                    </div> */}
                </Tab>
                <Tab eventKey="favorites" title="Favorites">
                    {/* <div className='table'>                
                        {columns.map(columnItem => (
                            <div className='cols' key={columnItem.key}>
                                <div className='colTitle'>{columnItem.title}</div>
                                <div className = 'rows'>
                                    {favoritsList.map((listItem, index) => <div className='rowItem' key={listItem.id}>
                                        {this.renderValue(columnItem, listItem, index)}
                                    </div>)}
                                </div>
                            </div>)
                        )}
                        {loading && <Spinner animation="border" variant="secondary" className='spinner'/>}
                    </div> */}
                </Tab>
            </Tabs>
        );
    }
}

function mapStateToProps(state) {
  const { Coins, CoinDetails } = state;
  return {
    list  : Coins.get('list'),
    UI    : Coins.get('UI'),
    detailsUI : CoinDetails.get('UI'),
  };
}
const mapDispatchToProps = {
  listReload : coinsActions.listReload,
  uiRefresh  : coinsActions.uiRefresh,
  uiDetailsRefresh  : coinsDetailsActions.uiRefresh,
}
export default connect(mapStateToProps, mapDispatchToProps)(List);
