import React from 'react';
import { connect } from 'react-redux';
import coinsActions from '../Redux/Coins/actions'
import CoinsTable from '../containers/Coins'
class App extends React.Component {

    constructor(props) {
        super(props);

        //Initialise state
        this.state = {
            counter: 0
        };
    }


    static getInitialProps({store}) {console.log(store)}

    componentDidMount(){
        const {listReload} = this.props;

        listReload();
    }
    //Updates the counter in state by +1
    incrementCounter = () => {
        this.setState(prevState => {
            this.setState({
                counter: prevState.counter + 1.
            });
        });
    };

    //Updates the counter in state by  -1
    decrementCounter = () => {
        this.setState(prevState => {
            this.setState({
                counter: prevState.counter - 1.
            });
        });
    };

    render() {
        return (
            <div>
                <CoinsTable />
            </div>
        );
    }
}

function mapStateToProps(state) {

  return {
    entities : state.Coins.get('list')
  };
}
const mapDispatchToProps = {
  listReload : coinsActions.listReload,
}
export default connect(mapStateToProps, mapDispatchToProps)(App);
