import React from 'react';
import { connect } from 'react-redux';
import coinsActions from '../../Redux/Coins/actions'
import { Table, Tag } from 'antd';
import {columns} from './utils'
import PropTypes from 'prop-types';
import { Spin } from 'antd';

class List extends React.Component {

    static propTypes = {
        list        : PropTypes.array.isRequired,
        UI          : PropTypes.object.isRequired,
        listReload  : PropTypes.func.isRequired,
        uiRefresh   : PropTypes.func.isRequired,
    }

    static getInitialProps() {}

    componentDidMount(){
        const {listReload} = this.props;
        listReload();
    }

    loadMoreCoins = () => {
        const { listReload, uiRefresh, UI } = this.props;
        const { skip } = UI;
        uiRefresh({
            skip : skip + 1, 
        })
        listReload();
    }

    render() {
        const { list, UI } = this.props;
        const { loading } = UI;
        const antIcon = <LoadingOutlined style={{ fontSize: 24 }} spin />;
        return (
            <>
                <div className='table'>                
                    {/* <Table columns={columns} dataSource={list} pagination = {false} loading={loading}/> */}
                    {columns.map(item => (
                        <div className='cols' key={item.key}>
                            <div className='colTitle'>{item.title}</div>
                            <div className = 'rows'>
                                {list.map(listItem => <div className='rowItem' key={listItem.id}>
                                    {listItem[item.dataIndex]}
                                </div>)}
                            </div>
                        </div>)
                    )}
                    {
                        true && <Spin />
                    }
                </div>
                <button onClick={this.loadMoreCoins}>Show More</button>
            </>
        );
    }
}

function mapStateToProps(state) {

  return {
    list    : state.Coins.get('list'),
    UI : state.Coins.get('UI')
  };
}
const mapDispatchToProps = {
  listReload : coinsActions.listReload,
  uiRefresh  : coinsActions.uiRefresh,
}
export default connect(mapStateToProps, mapDispatchToProps)(List);
