import React from 'react';
import { connect } from 'react-redux';
import coinsActions from '../Redux/Coins/actions'

class App extends React.Component {

    constructor(props) {
        super(props);

        //Initialise state
        this.state = {
            counter: 0
        };
    }

    componentdidmount(){
        const {listReload} = this.props;
        listReload();
    }

    //Updates the counter in state by +1
    incrementCounter = () => {
        this.setState(prevState => {
            this.setState({
                counter: prevState.counter + 1.
            });
        });
    };

    //Updates the counter in state by  -1
    decrementCounter = () => {
        this.setState(prevState => {
            this.setState({
                counter: prevState.counter - 1.
            });
        });
    };

    render() {
        return (
            <div>
                <button onClick={this.incrementCounter}>Increment</button>
                <button onClick={this.decrementCounter}>Decrement</button>
                <h1>{this.state.counter}</h1>
            </div>
        );
    }
}

function mapStateToProps(state) {
  const { locale } = deriveLanguage(state);
  const { themeName } = deriveTheme(state);

  return {
    locale,
    selectedTheme      : themeName,
    idToken            : deriveToken(state),
    activeTabTitle     : deriveActiveTabTitle(state),
    notificationsCount : deriveAllNotificationsCount(state),
  };
}
const mapDispatchToProps = {
  listReload : coinsActions.listReload,
}
export default connect(null, mapDispatchToProps)(App);
