import React from 'react';
import { connect } from 'react-redux';
import coinsActions from '../../Redux/CoinDetails/actions'
import PropTypes from 'prop-types';
import { Spinner} from 'react-bootstrap'

class Details extends React.Component {

    static propTypes = {
        baseData    : PropTypes.object.isRequired,
        UI          : PropTypes.object.isRequired,
        dataReload  : PropTypes.func.isRequired,
        uiRefresh   : PropTypes.func.isRequired,
        id          : PropTypes.string.isRequired,
    }

    static getInitialProps() {}

    componentDidMount(){
        const { dataReload, id} = this.props;
        dataReload(id);
    }

    render() {
        const {baseData, UI} = this.props;
        const {availableSupply, exp, icon, name, price, totalSupply, twitterUrl, websiteUrl, volume, marketCap, symbol} = baseData;
        const {loading} = UI;
        if(loading) {
            return <Spinner animation="border" variant="secondary" className='spinner'/>
        }
        return (
            <>
                <div className='title'>
                    <h3><img src={icon} />{`${name} (${symbol})`}</h3>
                </div>
                <div className = 'details'>
                    <div>
                        <div className='cup'>
                            <span className='heading'>MARKET CAP</span>
                            <span>{marketCap}</span>
                        </div>
                        <div classname='volume'>
                            <span className='heading'>VOLUME 24H</span>
                            <span>{volume}</span>
                        </div>
                    </div>
                    <div>
                        <div className='avSuply'>
                            <span className='heading'>AVAILABLE SUPPLY</span>
                            <span>{availableSupply}</span>
                        </div>
                        <div className='volume'>
                            <span className='heading'>TOTAL SUPPLY</span>
                            <span>{totalSupply}</span>
                        </div>
                    </div>
                    <div className='websites'>
                        <span className='heading'>MARKET CAP</span>
                        <span>{websiteUrl}</span>
                        <span>{twitterUrl}</span>
                    </div>
                    <div className='explore'>
                        {exp && exp.map((item, index) => <a href={item}>{`Explore ${index + 1}`}</a>)}
                    </div>
                </div>
            </>
        );
    }
}

function mapStateToProps(state) {
  
  return {
    baseData : state.CoinDetails.get('baseData'),
    UI : state.CoinDetails.get('UI')
  };
}
const mapDispatchToProps = {
  dataReload : coinsActions.baseDataReload,
  uiRefresh  : coinsActions.uiRefresh,
}
export default connect(mapStateToProps, mapDispatchToProps)(Details);
