import { toInteger, keys, cloneDeep, isArray, isEmpty } from 'lodash';

// check if value is ID: integer number > 0
export function isID(value) {
  return Boolean(toInteger(value));
}

// filling object
export function fill(source, target, withCreatingKeys = false) {

  const sourceKeys = keys(source);
  const targetKeys = keys(target);
  const result = cloneDeep(target);
  sourceKeys.forEach(key => {
    if (!targetKeys.includes(key) && !withCreatingKeys) {
      return;
    }
    result[key] = source[key];
  });
  return result;
}

// list convert to entities
export function listToEntities(list = [], nameKey = 'id') {
  if (!isArray(list) || isEmpty(list)) {
    return {};
  }

  const result = {};

  list.forEach(item => {
    const keyValue = item[nameKey];
    if (!keyValue) {
      return;
    }

    result[keyValue] = cloneDeep(item);
  });

  return result;
}

export const addFavoritList = (id) => {
  let favorits = localStorage.getItem('favorits');
  if(favorits) {
    favorits = JSON.parse(favorits);
  } else {
    favorits = []
  }
  const haveFavorits = favorits.includes(id);
  if (haveFavorits) {
    favorits = favorits.filter(item => item !== id);
  } else {
    favorits.push(id)
  }
  localStorage.setItem('favorits', JSON.stringify(favorits))
  return favorits;
}

export const getFavorits = () => {
  let favorits = localStorage.getItem('favorits');
  if(favorits) {
      favorits = JSON.parse(favorits);
  } else {
      favorits = []
  }
  return 
}
