import React from 'react';
import { connect } from 'react-redux';
import coinsActions from '../../Redux/CoinDetails/actions'
import PropTypes from 'prop-types';
import { Spinner} from 'react-bootstrap'

class Details extends React.Component {

    static propTypes = {
        baseData    : PropTypes.object.isRequired,
        UI          : PropTypes.object.isRequired,
        dataReload  : PropTypes.func.isRequired,
        uiRefresh   : PropTypes.func.isRequired,
        id          : PropTypes.string.isRequired,
    }

    static getInitialProps() {}

    componentDidMount(){
        const { dataReload, id} = this.props;
        dataReload(id);
    }

    render() {
        const {availableSupply, exp, icon, name, price, totalSupply, twitterUrl, websiteUrl, volume, marketCap, loading} = this.props.baseData;
        if(loading) {
            return <Spinner animation="border" variant="secondary" className='spinner'/>
        }
        return (
            <div className = 'details'>
                <div className='title'>
                    <img src={icon} />
                    {name}
                </div>
                <div className='cap'>
                    {marketCap}
                    {volume}
                </div>
                <div className='avSuply'>
                    {availableSupply}
                    {totalSupply}
                </div>
                <div className='websites'>
                    {websiteUrl}
                    {twitterUrl}
                </div>
                <div className='explore'>
                    {exp && exp.map((item, index) => <a href={item}>{`Explore ${index + 1}`}</a>)}
                </div>
            </div>
        );
    }
}

function mapStateToProps(state) {
  
  return {
    baseData : state.CoinDetails.get('baseData'),
    UI : state.CoinDetails.get('UI')
  };
}
const mapDispatchToProps = {
  dataReload : coinsActions.baseDataReload,
  uiRefresh  : coinsActions.uiRefresh,
}
export default connect(mapStateToProps, mapDispatchToProps)(Details);
