import { all, takeEvery, put, fork, call, select } from 'redux-saga/effects';
import actions from './actions';

import { coinsAPI } from '../../helpers/api/coins';

const messages = {
  errorListLoad : 'Loading Coins list failed',
};

function getStoreData(state) {
  const { CoinDetails } = state;
  const UI = Coins.get('UI');
  const { id } = UI;

  return {
    id,
  };
}

function* dataReload() {
  yield takeEvery(actions.BASE_DATA_RELOAD, function* () {
    yield put(actions.uiRefresh({ loading: true }));
    const { id } = yield select(getStoreData);

    try {
      const res = yield call(coinsAPI.coinDetails, id);
      if (res && res.status === 200) {
        yield put(actions.baseDataRerash(res.data.coins));
      };
    } catch (error) {
      alert(messages.errorListLoad, error);
      console.log(error);
    }

    yield put(actions.uiRefresh({ loading: false }));
  });
}

export default function* detailsSagas() {
  yield all([fork(dataReload)]);
}
