import { connect } from 'react-redux';
import coinsActions from '../../Redux/Coins/actions'
import { Table, Tag } from 'antd';
import {columns} from './utils'
import PropTypes from 'prop-types';

class List extends React.Component {
    static propTypes = {
        list : PropTypes.array.isRequired,
        loading : PropTypes.bool.isRequired,
    }
    static getInitialProps({store}) {}

    componentDidMount(){
        const {listReload} = this.props;
        listReload();
    }

    render() {
        const {list, loading} = this.props;
        return (
            <div className='table'>                
                {/* <Table columns={columns} dataSource={list} pagination = {false} loading={loading}/> */}
                {columns.map((item, index) => (
                    <div className='cols' key={item.key}>
                        <div className='colTitle'>{item.title}</div>
                        <div className = 'rows'>
                            {list.map(listItem => <div className='rowItem' key={listItem.id}>{listItem[item.dataIndex]}</div>)}
                        </div>
                    </div>)
                )}
            </div>
        );
    }
}

function mapStateToProps(state) {

  return {
    list : state.Coins.get('list'),
    loading : state.Coins.get('UI').loading
  };
}
const mapDispatchToProps = {
  listReload : coinsActions.listReload,
}
export default connect(mapStateToProps, mapDispatchToProps)(List);
